mapper.select:
        SELECT


--         级联表的字段
        ,saishi.saishi_name as saishiName
        ,saishi.saishi_photo as saishiPhoto
        ,saishi.saishi_types as saishiTypes
        ,saishi.saishi_old_money as saishiOldMoney
        ,saishi.saishi_new_money as saishiNewMoney
        ,saishi.zuowei_number as zuoweiNumber
        ,saishi.saishi_clicknum as saishiClicknum
        ,saishi.shangxia_types as shangxiaTypes
        ,saishi.saishi_delete as saishiDelete
        ,saishi.saishi_content as saishiContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney

        FROM saishi_order  a
        left JOIN saishi saishi ON a.saishi_id = saishi.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.saishi_order_uuid_number like CONCAT('%',#{params.saishiOrderUuidNumber},'%')


                and (
                    a.saishi_id = #{params.saishiId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                  and a.saishi_order_true_price >= #{params.saishiOrderTruePriceStart}   


                  and a.saishi_order_true_price <= #{params.saishiOrderTruePriceEnd}   


                and a.saishi_order_types = #{params.saishiOrderTypes}


                and a.saishi_order_payment_types = #{params.saishiOrderPaymentTypes}


                and a.buy_zuowei_number like CONCAT('%',#{params.buyZuoweiNumber},'%')


                  and UNIX_TIMESTAMP(a.buy_zuowei_time) >= UNIX_TIMESTAMP(#{params.buyZuoweiTimeStart}) 


                  and UNIX_TIMESTAMP(a.buy_zuowei_time) <= UNIX_TIMESTAMP(#{params.buyZuoweiTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.saishi_id IS NOT NULL


                and saishi.saishi_name like CONCAT('%',#{params.saishiName},'%')


                and saishi.saishi_types = #{params.saishiTypes}



                  and saishi.saishi_old_money >= #{params.saishiOldMoneyStart}   


                  and saishi.saishi_old_money <= #{params.saishiOldMoneyEnd}   


                  and saishi.saishi_new_money >= #{params.saishiNewMoneyStart}   


                  and saishi.saishi_new_money <= #{params.saishiNewMoneyEnd}   


                  and saishi.zuowei_number >= #{params.zuoweiNumberStart}   


                  and saishi.zuowei_number <= #{params.zuoweiNumberEnd}   


                and saishi.zuowei_number = #{params.zuoweiNumber}


                  and saishi.saishi_clicknum >= #{params.saishiClicknumStart}   


                  and saishi.saishi_clicknum <= #{params.saishiClicknumEnd}   


                and saishi.saishi_clicknum = #{params.saishiClicknum}


                and saishi.shangxia_types = #{params.shangxiaTypes}



                  and saishi.saishi_delete >= #{params.saishiDeleteStart}   


                  and saishi.saishi_delete <= #{params.saishiDeleteEnd}   


                and saishi.saishi_delete = #{params.saishiDelete}


                and saishi.saishi_content like CONCAT('%',#{params.saishiContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   



        order by a.${params.orderBy} desc
